'use strict';

const fs = require('fs');
const path = require('path');
const child_process = require('child_process');
const util = require('util')

module.exports.version = '0.0.1';

const utils = module.exports;

if(! Buffer.alloc) Buffer = require('safe-buffer').Buffer;

module.exports.init = function(options) {
    if((options || {}).log){
        let processFile = path.parse(process.mainModule.filename);
        let logDir = `${processFile.dir}/logs`;
        if (!fs.existsSync(logDir))
            fs.mkdirSync(logDir);
        module.logFileName = `${logDir}/${processFile.name}.log`;
    }


    initExitHandler();

    return module.exports;
};

const savedLog = {};
Object.defineProperty(savedLog, 'count', {
    enumerable: false,
    writable: true,
    value: 0
});

module.exports.beginLog = function(){
    savedLog[++savedLog.count] = '';
    return savedLog.count;
};

module.exports.endLog = function(handle){
    let res = savedLog[handle];
    delete savedLog[handle];
    return res;
} ;

function write_file_log(text){
    //console.log(text);
    if(module.logFileName) {
        let tl = utils.DateToShortXMLString(new Date());
        fs.appendFileSync(module.logFileName, `${tl}\t${text}\n`);
    }

    for(let key in savedLog){
        savedLog[key] += `${text}\n`;
    }
}

function mixin(target, source) {
    Object.keys(source).forEach(name=>{
        if(target.hasOwnProperty(name) && target[name] instanceof Object) {
            if('function' === typeof target[name]){
                let res = function(){
                    let r = target[name];
                    r.r1.apply(this, arguments);
                    r.r2.apply(this, arguments);
                };
                res.r1 = target[name], res.r2 = source[name];
                target[name] = res; //
            }else
                mixin(target[name], source[name]);
        }else
            target[name] = source[name];

    });
}
module.exports.mixin = mixin;


    

function dataRepresentationForLog(arg) {
    if(arg instanceof Error)
        return arg.stack;
    else if(arg instanceof Object) // 'object' === typeof
        return util.inspect(arg, {showHidden: false, depth: null});
    return arg;
}

console._log = console.log;
console.log = function (arg, arg2) {
    if(arg instanceof Error)
        console.error(arg, arg2);
    else
        console._log(`\x1b[2K${dataRepresentationForLog(arg)}${(arg2 === undefined ? '' : dataRepresentationForLog(arg2))}\x1b[0m`);
};
console._error = console.error;
console.error = function (arg, arg2) {
    arg = dataRepresentationForLog(arg) + (arg2 === undefined ? '' : dataRepresentationForLog(arg2));
    console._error(`\x1b[2K\x1b[91m${arg}\x1b[0m`);
    write_file_log(arg);
};
console._info = console.info;
console.info = function (arg, arg2) {
    arg = dataRepresentationForLog(arg) + (arg2 === undefined ? '' : dataRepresentationForLog(arg2));
    console._info(`\x1b[2K\x1b[94m${arg}\x1b[0m`);
    write_file_log(arg);
};
console._warn = console.warn;
console.warn = function (arg, arg2) {
    arg = dataRepresentationForLog(arg) + (arg2 === undefined ? '' : dataRepresentationForLog(arg2));
    console._warn(`\x1b[2K\x1b[93m${arg}\x1b[0m`);
    write_file_log(arg);
};
console.state = function (arg, arg2) {
    arg = dataRepresentationForLog(arg) + (arg2 === undefined ? '' : dataRepresentationForLog(arg2));
    console._log(`\x1b[2K\x1b[92m${arg.replaceAll('\n', '')}\x1b[1A\x1b[0m`)
};



function byte(x){
    return x < 0 ? x + 256 : x;
}
module.exports.byte = byte;


module.exports.DateToShortXMLString = function (d){
    return Date.make(d).toFormatString('yyyymmddThhiiss');
};


Number.prototype.toHex = function(len){
    let res = Number(this).toString(16);
    return String('00000000' + res).slice(-Math.max(len || 0, Math.ceil(res.length / 2) * 2));
};
Number.prototype.toBin = function(len){
    return String('0000000000000000' + Number(this).toString(2)).slice(-len);
};


Array.prototype.toBin = function() {
    let res = '';
    this.forEach(function (entry) {
        res += Number(byte(entry)).toBin(8) + ' ';
    });

    return res;
};

Array.prototype.toHex = function() {
    let res = '';
    this.forEach(function (entry) {
        res += Number(byte(entry)).toHex() + ' ';
    });

    return res;
};

Date.make = function (val) { //dateFromString
    if( val instanceof Date)
        return val;

    let res, _val = String(val);
    if(_val.slice(8, 9) === 'T' && _val.length === 15)
        res = new Date(_val.substr(0, 4), Number(_val.substr(4, 2)) - 1, _val.substr(6, 2), _val.substr(9, 2), _val.substr(11, 2), _val.substr(13, 2));
    else if(_val.slice(0, 1) === '1' && _val.length === 10)
        res = new Date(1000 * _val);
    else if(_val.slice(10, 11) === 'T')
        res = new Date(_val);
    else if(_val.slice(0, 1) === '1' && _val.length === 13)
        res = new Date(Number(_val));
    else if (_val === '0')
        res = new Date(0);
    else
        throw new Error(`Unknown date format: ${_val}`);

    return res;
};


Date.prototype.toFormatString = function(format, utc) {
    if(format === undefined)
        return this.toString();
    else if(utc){
        let yyyy = this.getUTCFullYear().toString();
        format = format.replace(/yyyy/g, yyyy);
        let mm = (this.getUTCMonth() + 1).toString();
        format = format.replace(/mm/g, (mm[1] ? mm : "0" + mm[0]));
        let dd = this.getUTCDate().toString();
        format = format.replace(/dd/g, (dd[1] ? dd : "0" + dd[0]));
        let hh = this.getUTCHours().toString();
        format = format.replace(/hh/g, (hh[1] ? hh : "0" + hh[0]));
        let ii = this.getUTCMinutes().toString();
        format = format.replace(/ii/g, (ii[1] ? ii : "0" + ii[0]));
        let ss = this.getSeconds().toString();
        format = format.replace(/ss/g, (ss[1] ? ss : "0" + ss[0]));

    }else {
        let yyyy = this.getFullYear().toString();
        format = format.replace(/yyyy/g, yyyy);
        let mm = (this.getMonth() + 1).toString();
        format = format.replace(/mm/g, (mm[1] ? mm : "0" + mm[0]));
        let dd = this.getDate().toString();
        format = format.replace(/dd/g, (dd[1] ? dd : "0" + dd[0]));
        let hh = this.getHours().toString();
        format = format.replace(/hh/g, (hh[1] ? hh : "0" + hh[0]));
        let ii = this.getMinutes().toString();
        format = format.replace(/ii/g, (ii[1] ? ii : "0" + ii[0]));
        let ss = this.getSeconds().toString();
        format = format.replace(/ss/g, (ss[1] ? ss : "0" + ss[0]));
    }
    return format;
};

String.prototype.replaceAll = function(search, replacement) {
    return this.split(search).join(replacement);
};

String.prototype.toKebab = function(){
    return this
        .replace(/([a-z0-9])([A-Z])/g, '$1-$2')
        .replace(/([A-Z])([A-Z])(?=[a-z])/g, '$1-$2')
        .toLowerCase();
};

String.prototype.toCamel = function(){
    let res = this.replace(/[-_]([a-z])/g, function (g) { return g[1].toUpperCase(); })
    return res[0].toLowerCase() + res.slice(1);
};
String.prototype.toPascal = function(){
    let res = this.replace(/[-_]([a-z])/g, function (g) { return g[1].toUpperCase(); })
    return res[0].toUpperCase() + res.slice(1);
};

if(typeof Promise.prototype.finally !== 'function') {
    Promise.prototype.finally = function (fn) {
        return this
            .then(value => this.constructor.resolve(fn()).then(() => value))
            .catch(reason => this.constructor.resolve(fn()).then(() => { throw reason }))
    }
}


function initExitHandler(exitHandler) {
//do something when app is closing
    process.on('exit', (code) => {
        let msg = `Done. Exit code: ${code}`;
        if(code)
            console.error(msg);
        else
            console.info(msg);
    });

//catches ctrl+c event
    process.on('SIGINT', (signal)=>{
        console.info(`Receive SIGINT (ctrl+c)`);
        process.exit(0);
    });

// catches "kill pid" (for example: nodemon restart)
//    process.on('SIGUSR1', exitHandler.bind(null, {event:'SIGUSR1', exit: true}));
//    process.on('SIGUSR2', exitHandler.bind(null, {event:'SIGUSR2', exit: true}));

    process.on('unhandledRejection', (reason, p) => {
        console.error('unhandledRejection:');
        console.error(reason);
        process.exit(2);
    });
    process.on('uncaughtException', err => {
        console.error('uncaughtException:');
        console.error(err);
        process.exit(1);
    });
}

module.exports.spawnAnything = function (apps, cb, opt) {
    for(let key in apps){
        let child = child_process.spawn(key, apps[key], opt);
        child.on('error', (err) => {
            console.log(`Failed to start child process '${child.spawnargs.join(' ')}' (${err.message})`);
        });
        if(child.pid){
            child._stdout = '';
            child.stdout.on('data', (data) => {
                child._stdout += (data ? new Buffer(data,'utf-8') : '').toString();
            });
            child._stderr = '';
            child.stderr.on('data', (data) => {
                child._stderr += (data ? new Buffer(data,'utf-8') : '').toString();
            });

            child.on('close', (code) => {
                cb(child, child._stdout, child._stderr);
            });
            return child;
        }
    }
};

